// Welcoming image placeholders for a dental practice
// These replace clinical-looking placeholders with warm, inviting ones

// Base64 encoded blur placeholder of a smiling person (warm and welcoming)
export const welcomingBlurDataURL =
  "data:image/jpeg;base64,";

// Warm dental office blur placeholder
export const dentalOfficeBlurDataURL =
  "data:image/jpeg;base64,";

// Happy family blur placeholder
export const happyFamilyBlurDataURL =
  "data:image/jpeg;base64,";

// Professional dental team blur placeholder
export const dentalTeamBlurDataURL =
  "data:image/jpeg;base64,";

// Welcoming placeholder configurations
export const placeholderConfig = {
  // For staff photos
  staff: {
    blurDataURL: welcomingBlurDataURL,
    fallbackAlt: "Professional dental team member",
  },

  // For patient photos
  patient: {
    blurDataURL: happyFamilyBlurDataURL,
    fallbackAlt: "Happy dental patient",
  },

  // For office/practice photos
  office: {
    blurDataURL: dentalOfficeBlurDataURL,
    fallbackAlt: "Welcoming dental practice",
  },

  // For general dental content
  general: {
    blurDataURL: dentalTeamBlurDataURL,
    fallbackAlt: "Professional dental care",
  },
};

// Helper function to get appropriate placeholder
export function getWelcomingPlaceholder(
  type: keyof typeof placeholderConfig = "general",
) {
  return placeholderConfig[type];
}

// CSS classes for welcoming loading states
export const welcomingLoadingClasses = {
  base: "animate-pulse bg-gradient-to-br from-pear-background/20 to-soft-pink/10 rounded-lg",
  withBorder:
    "animate-pulse bg-gradient-to-br from-pear-background/20 to-soft-pink/10 rounded-lg border border-gray-100 shadow-sm",
  card: "animate-pulse bg-gradient-to-br from-white/80 to-pear-background/10 rounded-xl border border-gray-100 shadow-sm",
  text: "animate-pulse bg-gradient-to-r from-pear-background/30 to-soft-pink/20 rounded",
};
